import jsonimport requestsdef cec_uac_header(userId):    uac_url = "http://auth.test.cecdat.com/usercenter/v2/api/token/generate/token"    params = {        "userId": userId    }    headers = {        "Content-Type": "application/json; charset=UTF-8",    }    r = requests.get(url=uac_url, params=params, headers=headers)    access_token = r.json()['data']    print(access_token)    header_str = json.dumps({'authorization': access_token})    return header_strif __name__ == '__main__':    print(cec_uac_header(2142529660922101760))